/// <reference types="node" />
declare const fs: {
	writeFileSync: (path: string, data: string) => void;
	existsSync: (path: string) => boolean;
	readFileSync: (path: string) => string | Buffer;
	readdirSync: (path: string) => string[];
	mkdirSync: (
		path: string,
		opts?: {
			recursive: boolean;
		}
	) => void;
	statSync: (path: string) => {
		isFile: () => boolean;
		isDirectory: () => boolean;
	};
};
export = fs;
